mapper.insert:
insert into comment_tag (id, name, type_id, 
  rank, status, create_time
  )
values (#{id,jdbcType=BIGINT}, #{name,jdbcType=VARCHAR}, #{typeId,jdbcType=BIGINT}, 
  #{rank,jdbcType=INTEGER}, #{status,jdbcType=INTEGER}, #{createTime,jdbcType=TIMESTAMP}
  )

mapper.select:
select count(*) from comment_tag


    and id = #{id,jdbcType=BIGINT}


    and name = #{name,jdbcType=VARCHAR}


    and type_id = #{typeId,jdbcType=BIGINT}


    and rank = #{rank,jdbcType=INTEGER}


    and status = #{status,jdbcType=INTEGER}


    and create_time = #{createTime,jdbcType=TIMESTAMP}

select 

from comment_tag where 1 = 1 

  and id = #{param1.id,jdbcType=BIGINT}


  and name = #{param1.name,jdbcType=VARCHAR}


  and type_id = #{param1.typeId,jdbcType=BIGINT}


  and rank = #{param1.rank,jdbcType=INTEGER}


  and status = #{param1.status,jdbcType=INTEGER}


  and create_time = #{param1.createTime,jdbcType=TIMESTAMP}


  order by 

    ${order.property} ${order.direction}



  limit ${param2.offset}, ${param2.pageSize}
