mapper.update:
update doctor_invite_log
set doctor_id = #{doctorId,jdbcType=BIGINT},
  content = #{content,jdbcType=VARCHAR},
  create_time = #{createTime,jdbcType=TIMESTAMP}
where id = #{id,jdbcType=BIGINT}

mapper.select:
select count(*) from doctor_invite_log


    and id = #{id,jdbcType=BIGINT}


    and doctor_id = #{doctorId,jdbcType=BIGINT}


    and content = #{content,jdbcType=VARCHAR}


    and create_time = #{createTime,jdbcType=TIMESTAMP}

select 

from doctor_invite_log where 1 = 1 

  and id = #{param1.id,jdbcType=BIGINT}


  and doctor_id = #{param1.doctorId,jdbcType=BIGINT}


  and content = #{param1.content,jdbcType=VARCHAR}


  and create_time = #{param1.createTime,jdbcType=TIMESTAMP}


  order by 

    ${order.property} ${order.direction}



  limit ${param2.offset}, ${param2.pageSize}
